import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import {useDispatch, useSelector} from 'react-redux';
import { getCountryData } from '../../actions';
import './Detail.css'
import { Link } from 'react-router-dom';
import Loader from '../Loader/Loader';

export default function Detail() {
    const [isLoading, setIsLoading] = useState(false)
    const {id} = useParams()
    const dispatch = useDispatch()
    const country = useSelector((state)=> state.countryDetail)

    useEffect(()=>{
      setIsLoading(true)
        dispatch(getCountryData(id))
        setTimeout(() => {
          setIsLoading(false)
        }, 2200);
    }, [dispatch, id])

    if(isLoading){
      return <Loader/>
    }

    const name = country.map((e)=>e.name)
    const continent = country.map((e)=>e.continent)
    const population = country.map((e)=>e.population)
    const area = country.map((e)=>e.area)
    const subregion = country.map((e)=>e.subregion)
    const capital = country.map((e)=>e.capital)
    const flag = country.map((e)=>e.flag)
  return (
    <div className='box'>
        <div className='topbar'>
           <div className='country'>
           <img src={flag} alt={name + 'flag'}  width={100}/>
            <h1>{name}</h1>
           </div>           
        </div>
        <div className='details'>
          <img className='img2' src={flag} alt="" />
          <h3 className='title'>General Information</h3>
          <div className='box-detail'>
            <div className='text'>
              <div className='detail'>Continent: </div>
              <div className='data'>{continent}</div>
            </div>
            <div className='text'>
              <div className='detail'>Subregion: </div>
              <div>{subregion}</div>
            </div>
            <div className='text'>
              <div className='detail'>Capital: </div>
              <div>{capital}</div>
            </div >
            <div className='text'>
              <div className='detail'>Area:</div>
              <div> {area}</div>
            </div>
            <div className='text'>
              <div className='detail'>Pupulation:{' '} </div>
              
              <div>{population.toLocaleString()} </div>
            </div>
            
            
            
            
            
          </div>
          

        </div>

           <Link to={'/home'}><button className='btn'>Back</button></Link> 
    </div>
  )
}
