import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { createActivity, getCountries } from '../../actions'
import './CreateActivity.css'

function validate(input){
        const errors = {}
    if(input.name === ''){
        errors.name = 'name must be completed'
    }
    else if(input.difficulty.length !== 0) {
        errors.difficulty = 'difficulty must be completed'
    }
    return errors
}


export default function CreateActivity() {
    const [error, setError] = useState({})
    const history = useHistory()
    const country = useSelector((state)=> state.countries)
    const postAct = useSelector((state)=> state.post)
    const dispatch = useDispatch()
    useEffect(()=>{
        dispatch(getCountries())
    }, [dispatch])

    
    const [input, setInput] = useState({
        name : '',
        season : '',
        difficulty : '',
        duration : '',
        countries : []
    })

    const handleSeason = (e) => {
        setInput({
            ...input, season : e.target.value
        })
    }

    const handleChange = (e) => {
        setInput({
            ...input, [e.target.name] : e.target.value
            
        })
        setError(validate(input))
        
    }
    
    


    const countries = country.sort((a, b)=>{
        if(a.name > b.name){
           return  1
        }
        if (b.name > a.name){
            return -1
        }
        return 0
    })

    const handleDifficulty = (e) => {
        setInput({
            ...input, difficulty : e.target.value
        })
        setError(validate(input))
    }

    const handleDuration = (e) => {
        setInput({
            ...input, duration : e.target.value
        })
    }

    const handleCountries = (e) =>{
        setInput({
            ...input,
            countries : [...input.countries, input.countries.includes(e.target.value) ? null : e.target.value]
        })
    }

    const handleSubmit = (e)=>{
        e.preventDefault();
        
        if(Object.keys(error).length > 0){
           alert(Object.values(error))
        } else {
            dispatch(createActivity(input))
          setInput({
            name : '',
            season : '',
            difficulty : '',
            duration : '',
            countries : []
        })
        history.push('/home') 
        }
        
    }

    const handleCancel = (e)=>{
        e.preventDefault();
        setInput({
            name : '',
            season : '',
            difficulty : '',
            duration : '',
            countries : []
        })
    }

    const handleClick = (e) => {
        e.preventDefault()
        history.push('/home')
    }

    
    
    const countryList = countries.map((country)=>country.name)
    const season = ['Winter', 'Spring', 'Autumn', 'Summer']
    const difficulty = [1, 2, 3, 4 , 5]



  return (
    <div className='box'>

        <div className='container1'>
            <button onClick={handleClick}>Back</button>
        <form className='form' onSubmit={handleSubmit}>
            <div>
                <label>Activity: </label>
                <input type="text" value={input.name} name='name' onChange={handleChange}/> 
            </div>
            <div>
                <label>Season: </label>
                <input type="text" value={input.season} name='season' onChange={handleChange} />
            </div>
            <div>
                <label>Difficulty: </label>
                <input type="text" value={input.difficulty} name='difficulty' onChange={handleChange}/>
            </div>
            <div>
                <label>Duration: </label>
                <input type="text" value={input.duration} name='duration' onChange={handleChange} />
            </div>
            <div>
                <label>Countries: </label>
                <select onChange={handleCountries}>
                    <option value="" hidden>Select countries</option>
                    {
                        countryList.map((c)=>(
                            <option value={c} name='countries' key={c}>{c}</option>
                        ))
                    }
                </select>
                <p>{input.countries}</p>
            </div>
            
            <button className='button' onClick={handleSubmit}>Create activity</button>
            <button onClick={handleCancel}>Cancel</button>
        </form>
        </div>       
    </div>
  )
}
